    operationaltask.name,
    operationaltask.description,
    operationaltask.type,
    operationaltask.state,
    operationaltask.startdate,
    operationaltask.finishdate,
    (((staff.surname)::text || ' '::text) || (staff.name)::text) AS staffname,
