            i_1.quantity
           FROM (goodfood_extrusionpouringingredient i_1
             JOIN goodfood_extrusionpouring e ON ((i_1.extrusionpouring_id = e.id)))
        )
 SELECT row_number() OVER () AS id,
    i.extrusionprotocol_id,
    p.number AS productnumber,
