    resource.id AS resource_id
   FROM ((materialflowresources_resource resource
     JOIN deliveries_orderedproduct orderedproduct ON ((orderedproduct.product_id = resource.product_id)))
     JOIN deliveries_delivery delivery ON (((orderedproduct.delivery_id = delivery.id) AND (delivery.active = true) AND (delivery.location_id = resource.location_id) AND ((delivery.state)::text = ANY (ARRAY[('01draft'::character varying)::text, ('02prepared'::character varying)::text, ('03duringCorrection'::character varying)::text, ('05approved'::character varying)::text])))))
