    (resource.location_id)::integer AS location_id,
    (resource.product_id)::integer AS product_id,
    resource.quantity,
    COALESCE(orderedquantity.orderedquantity, (0)::numeric) AS orderedquantity,
    ( SELECT sum(warehouseminimalstate_warehouseminimumstate.minimumstate) AS sum
           FROM warehouseminimalstate_warehouseminimumstate
          WHERE ((warehouseminimalstate_warehouseminimumstate.product_id = resource.product_id) AND (warehouseminimalstate_warehouseminimumstate.location_id = resource.location_id))) AS minimumstate,
    resource.reservedquantity,
    resource.availablequantity
   FROM (materialflowresources_resourcestock resource
     LEFT JOIN materialflowresources_orderedquantitystock orderedquantity ON ((orderedquantity.resource_id = resource.id)))
  GROUP BY resource.location_id, resource.product_id, orderedquantity.orderedquantity, resource.reservedquantity, resource.availablequantity, resource.quantity;


