--
-- Name: materialflowresources_warehousestock; Type: VIEW; Schema: public; Owner: -
--

CREATE VIEW materialflowresources_warehousestock AS
 SELECT row_number() OVER () AS id,
    materialflowresources_resource.location_id,
    materialflowresources_resource.product_id,
    ( SELECT sum(warehouseminimalstate_warehouseminimumstate.minimumstate) AS sum
           FROM warehouseminimalstate_warehouseminimumstate
          WHERE ((warehouseminimalstate_warehouseminimumstate.product_id = materialflowresources_resource.product_id) AND (warehouseminimalstate_warehouseminimumstate.location_id = materialflowresources_resource.location_id))) AS minimumstate,
    ( SELECT COALESCE(sum(deliveries_orderedproduct.orderedquantity), (0)::numeric) AS sum
           FROM deliveries_orderedproduct,
            deliveries_delivery
          WHERE ((deliveries_orderedproduct.delivery_id = deliveries_delivery.id) AND (deliveries_delivery.active = true) AND (deliveries_delivery.location_id = materialflowresources_resource.location_id) AND (deliveries_orderedproduct.product_id = materialflowresources_resource.product_id) AND ((deliveries_delivery.state)::text = ANY (ARRAY[('01draft'::character varying)::text, ('02prepared'::character varying)::text, ('03duringCorrection'::character varying)::text, ('05approved'::character varying)::text])))) AS orderedquantity,
    sum(materialflowresources_resource.quantity) AS quantity
   FROM materialflowresources_resource
  GROUP BY materialflowresources_resource.location_id, materialflowresources_resource.product_id;


