    (min(helper.dailyperformance))::numeric(14,2) AS dailyperformance
   FROM (productioncounting_performanceanalysisdetaildto performanceanalysisdetaildto
     LEFT JOIN productioncounting_performanceanalysisdto_helper helper ON (((performanceanalysisdetaildto.staff_id = helper.staff_id) AND (performanceanalysisdetaildto.timerangefromwithouttime = helper.timerangefromwithouttime))))
